import "core-js/modules/web.dom.iterable";
import "core-js/modules/es6.regexp.to-string";
//import {deleteT,updateData1} from '../../api/index';
import { userDelete, userinsert, userupdate } from "../../api/index";
import { prolist, userselect, selectname } from "../../api/index";
import { postTest } from "../../api/index";
export default {
  name: 'basetable',
  data: function data() {
    return {
      query: {
        userName: '',
        authority: '',
        status: '',
        page: 1,
        pagesize: 8
      },
      tableData: [],
      multipleSelection: [],
      multipleSelectionId: [],
      //new
      delList: [],
      editVisible: false,
      insertVisible: false,
      pageTotal: 0,
      form: {
        userName: '',
        userNo: '',
        password: '',
        authority: '',
        status: '',
        phone: ''
      },
      idx: -1,
      id: -1,
      testId: '',
      formData: '',
      searchData: [],
      selectExecutor: "",
      editId: '',
      missionCount: '',
      edit: {
        status: '',
        executorName: ''
      },
      //时间选择器数据
      pickerOptions: {
        shortcuts: [{
          text: '今天',
          onClick: function onClick(picker) {
            picker.$emit('pick', new Date());
          }
        }, {
          text: '昨天',
          onClick: function onClick(picker) {
            var date = new Date();
            date.setTime(date.getTime() - 3600 * 1000 * 24);
            picker.$emit('pick', date);
          }
        }, {
          text: '一周前',
          onClick: function onClick(picker) {
            var date = new Date();
            date.setTime(date.getTime() - 3600 * 1000 * 24 * 7);
            picker.$emit('pick', date);
          }
        }]
      },
      editTime: '',
      editTime1: '',
      insert: {
        userNo: '',
        userName: '',
        password: '',
        authority: '',
        status: '',
        image: '',
        phone: ''
      },
      namelist: [],
      //insert表单的规则
      //props和rules名字要与变量的名字保持一致
      rules: {
        userNo: [{
          required: true,
          message: '请输入用户编号',
          trigger: 'blur'
        }, {
          required: true,
          pattern: /^[1-9]\d*$/,
          message: '用户编号应为数字',
          trigger: 'blur'
        }, {
          min: 1,
          max: 5,
          message: '长度应在 1 到 5 个字符',
          trigger: 'blur'
        }],
        userName: [{
          required: true,
          message: '请输入用户姓名',
          trigger: 'blur'
        }, {
          min: 1,
          max: 15,
          message: '长度在 1 到 15 个字符',
          trigger: 'blur'
        }],
        password: [{
          required: true,
          message: '请输入密码',
          trigger: 'blur'
        }, {
          min: 6,
          max: 20,
          message: '长度应在 6 到 20 个字符',
          trigger: 'blur'
        }],
        authority: [{
          required: true,
          message: '请选择权限',
          trigger: 'change'
        }],
        status: [{
          required: true,
          message: '请选择状态',
          trigger: 'change'
        }
        // { min: 1, max: 8, message: '长度在 1 到 8 个字符', trigger: 'blur' }
        ],

        image: [{
          required: true,
          message: '请输入图片地址',
          trigger: 'blur'
        }],
        phone: [{
          required: true,
          message: '请输入电话',
          trigger: 'blur'
        }, {
          required: true,
          pattern: /^[1-9]\d*$/,
          message: '电话号码应为数字',
          trigger: 'blur'
        }
        // { min: 1, max: 8, message: '长度在 1 到 8 个字符', trigger: 'blur' }
        ]
      }
    };
  },
  created: function created() {
    this.getData();
  },
  methods: {
    // post(){
    //     postTest().then(res=>{
    //         console.log(res);
    //     })
    // },
    getpro: function getpro() {
      var _this = this;
      prolist().then(function (res) {
        _this.projectlist = res;
      });
    },
    getData: function getData() {
      var _this2 = this;
      userselect(this.query).then(function (res) {
        _this2.tableData = res.list;
        _this2.pageTotal = res.pageTotal; //|| 50;
      });
    },
    getdepartment: function getdepartment() {
      var _this3 = this;
      selectname().then(function (res) {
        _this3.departmentlist = res;
        //alert(this.departmentlist);
      });
    },
    getname: function getname() {
      var _this4 = this;
      selectname().then(function (res) {
        _this4.namelist = res;
      });
    },
    all: function all() {
      var _this5 = this;
      this.$set(this.query, 'page', 1);
      this.query.authority = '';
      this.query.userName = '';
      this.query.status = '';
      userselect(this.query).then(function (res) {
        _this5.tableData = res.list;
        _this5.pageTotal = res.pageTotal; //|| 50;
      });
    },
    // 触发搜索按钮this.query.status='';
    handleSearch: function handleSearch() {
      var _this6 = this;
      this.$set(this.query, 'page', 1);
      this.missionCount = 1;
      userselect(this.query).then(function (res) {
        _this6.tableData = res.list;
        _this6.pageTotal = res.pageTotal; //去掉了 || 50;
      });
    },
    selectPage: function (_selectPage) {
      function selectPage() {
        return _selectPage.apply(this, arguments);
      }
      selectPage.toString = function () {
        return _selectPage.toString();
      };
      return selectPage;
    }(function () {
      var _this7 = this;
      selectPage(0, 10).then(function (res) {
        _this7.tableData = res.list;
      });
    }),
    // 删除操作
    handleDelete: function handleDelete(index, row, rowId) {
      var _this8 = this;
      // 二次确认删除
      this.$confirm('确定要删除吗？', '提示', {
        type: 'warning'
      }).then(function () {
        _this8.$message.success('删除成功');
        _this8.tableData.splice(index, 1);
        userDelete(rowId);
      }).catch(function () {});
    },
    // 多选操作
    handleSelectionChange: function handleSelectionChange(val) {
      var _this9 = this;
      //清空上一次的多选情况
      //因为这一次可能有多选项被取消选中
      this.multipleSelection = [];
      this.multipleSelectionId = [];

      //alert(index);

      this.multipleSelection = val; //原始的
      //alert(this.multipleSelection);

      val.forEach(function (element) {
        _this9.multipleSelectionId.push(element.id);
      });
    },
    delAllSelection: function delAllSelection() {
      var length = this.multipleSelectionId.length;
      var str = '';
      var str1 = '';
      var num = length;
      this.delList = this.delList.concat(this.multipleSelectionId);
      //alert(this.delList);
      for (var i = 0; i < length; i++) {
        if (i != num - 1) {
          str += this.multipleSelectionId[i] + ','; //str为ID
          str1 += this.multipleSelection[i].userNo + '、';
        } else {
          str += this.multipleSelectionId[i];
          str1 += this.multipleSelection[i].userNo;
        }
      }
      userDelete(str);

      //this.$message.error(`删除了${str}`);
      this.$message.error("\u5220\u9664\u4E86\u7528\u6237".concat(str1, "\uFF0C\u5171").concat(num, "\u6761\u6570\u636E"));
      //this.$message.error(`删除了`);

      this.multipleSelection = [];
    },
    // 编辑操作
    handleEdit: function handleEdit(index, row, id) {
      //index 只是这条数据在前端中的数值
      this.idx = index;
      this.form = row;
      //alert(this.form.phone)
      this.editVisible = true;
      this.editId = id;
    },
    // 保存编辑
    saveEdit: function saveEdit(editId) {
      this.editVisible = false;
      this.$message.success("\u4FEE\u6539\u7B2C ".concat(this.idx + 1, " \u884C\u6210\u529F"));
      this.$set(this.tableData, this.idx, this.form);
      userupdate(this.form);
      //打补丁，否则修改第二个信息的时候会显示第一次输入                   
    },
    handleInsert: function handleInsert() {
      this.insertVisible = true;
    },
    submitForm: function submitForm(insertForm) {
      var _this10 = this;
      this.$refs[insertForm].validate(function (valid) {
        if (valid) {
          userinsert(_this10.insert);
          _this10.insertVisible = false;
          _this10.insert = {};
        } else {
          //console.log('error submit!!');
          return false;
        }
      });
    },
    resetForm: function resetForm(insertForm) {
      //重置表单内容
      this.$refs[insertForm].resetFields();
    },
    // 分页导航
    handlePageChange: function handlePageChange(val) {
      this.$set(this.query, 'page', val);
      this.getData();
    }
  }
};
